<style>
    html,body {
        margin: 0;
        padding: 0;
    }
    @font-face{font-family:"Gill Sans for Oriflame for Menu";font-weight:400;src:url(https://lifestyle.expert/fonts/GillSans_for_Oriflame/GillSans_for_Oriflame_Regular.woff) format('woff'),url(https://lifestyle.expert/fonts/GillSans_for_Oriflame/GillSans_for_Oriflame_Regular.ttf) format('truetype')}
    .top-menu{position:relative;z-index:600;background-color:#fff;font-family:'Gill Sans for Oriflame','Gill Sans for Oriflame for Menu',Verdana,sans-serif,Arial!important;min-width:1000px;line-height:1.16;-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale}.top-menu a{font-family:'Gill Sans for Oriflame','Gill Sans for Oriflame for Menu',Verdana,sans-serif,Arial!important;text-decoration:none}.top-menu a:not([href]):not([tabindex]){color:inherit;text-decoration:none}.top-menu__inner{height:50px;display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-align:center;-ms-flex-align:center;align-items:center}.top-menu__logo{width:50px;height:50px}.top-menu__list{margin-left:20px;height:100%}.top-menu__list ul{margin:0;padding:0}.top-menu__list ul li{position:relative;margin:0 10px;display:inline-block}.top-menu__list ul li:hover a{color:#f3cb8e}.top-menu__list ul li:hover a:before{opacity:1!important;visibility:visible!important}.top-menu__list ul li.active a{color:#000!important;cursor:default}.top-menu__list ul li.popup a:before{position:absolute;bottom:0;left:50%;margin-left:-12px;width:0;height:0;border-style:solid;border-width:0 12px 12px 12px;border-color:transparent transparent #f8f8f8 transparent;content:'';-webkit-transition:all .25s cubic-bezier(.645,.045,.355,1);transition:all .25s cubic-bezier(.645,.045,.355,1);opacity:0;visibility:hidden}.top-menu__list ul li>a,.top-menu__list ul li>span{font-size:16px;text-transform:uppercase;color:#9b9b9b;display:block;padding:15px 10px;line-height:20px;position:relative}.top-menu__list ul li a.home{position:relative;padding-right:0}.top-menu__list ul li a.home i{vertical-align:middle;display:inline-block;margin-top:-8px;margin-bottom:-2px;width:34px;height:30px;background-image:url("data:image/svg+xml,%3Csvg width='34' height='30' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M28.606 30h-9.025a.679.679 0 0 1-.678-.679V22.59h-4.476v6.731a.679.679 0 0 1-.679.679H4.723a.679.679 0 0 1-.678-.679V12.569c0-.238.124-.459.328-.582l11.942-7.186a.68.68 0 0 1 .7 0l11.941 7.186a.679.679 0 0 1 .329.582V29.32a.679.679 0 0 1-.679.679zm-8.346-1.358h7.667v-15.69L16.665 6.176 5.402 12.953v15.69h7.667V21.91c0-.375.304-.679.679-.679h5.833c.375 0 .68.304.68.679v6.731z' fill='%239B9B9B'/%3E%3Cpath d='M32.65 10.978a.676.676 0 0 1-.35-.097L16.666 1.47l-15.636 9.41a.679.679 0 1 1-.7-1.164L16.315.097a.68.68 0 0 1 .7 0L33 9.717a.679.679 0 0 1-.35 1.261z' fill='%239B9B9B'/%3E%3C/svg%3E");background-position:50% 50%;background-repeat:no-repeat;background-size:contain}.top-menu__list ul li:hover .header-global-popup{opacity:1;visibility:visible}.top-menu__list ul li.submenu>span{color:#9b9b9b;cursor:pointer}.top-menu__list ul li.submenu>span:before{position:absolute;top:50%;left:100%;margin-top:-4px;width:7px;height:7px;border-top:1px solid #9b9b9b;border-right:1px solid #9b9b9b;-webkit-transform:rotate(45deg);transform:rotate(45deg);-webkit-transition:all .1s;transition:all .1s;content:''}.top-menu__list ul li.submenu.active ul{display:block}.top-menu__list ul li.submenu.active>span{color:#f3cb8e}.top-menu__list ul li.submenu.active>span:before{-webkit-transform:rotate(135deg) translate3d(-2px,1px,0);transform:rotate(135deg) translate3d(-2px,1px,0);border-top-color:#f3cb8e;border-right-color:#f3cb8e}.top-menu__list ul li.submenu:hover>span{color:#f3cb8e}.top-menu__list ul li.submenu:hover>span:before{border-top-color:#f3cb8e;border-right-color:#f3cb8e}.top-menu__list ul li.submenu:hover ul{display:block}.top-menu__list ul li.submenu:hover>span{color:#f3cb8e}.top-menu__list ul li.submenu:hover>span:before{-webkit-transform:rotate(135deg) translate3d(-2px,1px,0);transform:rotate(135deg) translate3d(-2px,1px,0);border-top-color:#f3cb8e;border-right-color:#f3cb8e}.top-menu__list ul li.submenu ul{position:absolute;top:100%;left:-10px;background-color:#fff;display:none}.top-menu__list ul li.submenu ul li a{white-space:nowrap;cursor:pointer;color:#9b9b9b!important}.top-menu__list ul li.submenu ul li:hover a{color:#f3cb8e!important}.top-menu-popup{position:absolute;top:100%;left:50%;z-index:600;visibility:hidden;opacity:0;-webkit-transition:all .25s cubic-bezier(.645,.045,.355,1);transition:all .25s cubic-bezier(.645,.045,.355,1);min-width:665px;margin-left:-332px}.top-menu-popup__inner{padding:35px 50px 40px 30px;background-color:#f8f8f8}.top-menu-popup__pic{width:244px;display:inline-block;vertical-align:middle}.top-menu-popup__pic img{width:100%;height:auto}.top-menu-popup__content{width:300px;display:inline-block;vertical-align:middle;margin-left:35px}.top-menu-popup__title{font-family:'Garamond for Oriflame',Verdana,sans-serif,Arial!important;text-transform:uppercase;font-size:18px;font-weight:400;font-style:italic}.top-menu-popup__desc{margin-top:15px;font-size:14px;font-weight:300;line-height:13px}.top-menu-popup__button{margin-top:15px}@media (max-width:1279px){.top-menu__list{margin-left:0}.top-menu__list ul li{margin-left:3px;margin-right:3px}.top-menu__list ul li>a,.top-menu__list ul li>span{font-size:14px}.top-menu__list ul li a.home i{margin-left:5px;width:30px}.top-menu__list ul li.submenu ul li a{padding-top:10px;padding-bottom:10px}}
    /*# sourceMappingURL=global-desktop.min.css.map */

</style>

<div class="top-menu">
    <div class="top-menu__inner">
        <div class="top-menu__list">
            <ul>
                @foreach($items as $item)
                    <li {!! $item['active'] ? ' class="active" ' : (count($item['submenu']) > 0 ? ' class="submenu" ' : '') !!}>
                        @if (count($item['submenu']) > 0)
                            <span>{!! $item['title'] !!}</span>
                            <ul>
                                @foreach($item['submenu'] as $subitem)
                                    <li>
                                        <a href="{!! $subitem['href'] !!}" {!! $item['css_class'] ? ' class="' . $item['css_class'] . '" ' : '' !!} target="_blank" data-ga="{!! $subitem['ga'] !!}">
                                            {!! $subitem['hide_text'] == 0 ? $subitem['title'] : '<i></i>' !!}
                                        </a>
                                    </li>
                                @endforeach
                            </ul>
                        @else
                            <a href="{!! $item['href'] !!}" {!! $item['css_class'] ? ' class="' . $item['css_class'] . '" ' : '' !!} target="_blank" data-ga="{!! $item['ga'] !!}">
                                {!! $item['hide_text'] == 0 ? $item['title'] : '<i></i>' !!}
                            </a>
                        @endif
                    </li>
                @endforeach
            </ul>
        </div>
    </div>
</div>
