</style>

<div class="top-menu">
    <div class="top-menu__inner">
        <div class="top-submenu jsSubmenuMobile">
            <div class="top-submenu__bg jsSubmenuMobileClose"></div>
            <div class="top-submenu__body"></div>
        </div>
        <div class="top-menu__slider jsSliderNavMobile">
            <div class="swiper-container">
                <div class="swiper-wrapper">
                    @foreach($items as $item)
                        @if (count($item['submenu']) > 0)
                            <div class="swiper-slide submenu jsSubmenuMobileToggle">
                                <span>{!! $item['title'] !!}</span>
                                <ul>
                                    @foreach($item['submenu'] as $subitem)
                                        <li>
                                            <a href="{!! $subitem['href'] !!}" {!! $item['css_class'] ? ' class="' . $item['css_class'] . '" ' : '' !!} target="_blank" data-ga="{!! $subitem['ga'] !!}">
                                                {!! $subitem['hide_text'] == 0 ? $subitem['title'] : '<i></i>' !!}
                                            </a>
                                        </li>
                                    @endforeach
                                </ul>
                            </div>
                        @else
                            <div class="swiper-slide">
                                <a href="{!! $item['href'] !!}" {!! $item['css_class'] ? ' class="' . $item['css_class'] . '" ' : '' !!} target="_blank" {!! $item['active'] ? ' class="active" ' : '' !!} data-ga="{!! $item['ga'] !!}">
                                    {!! $item['hide_text'] == 0 ? $item['title'] : '<i></i>' !!}
                                </a>
                            </div>
                        @endif
                    @endforeach
                </div>
            </div>
        </div>
    </div>
</div>